// =============== Switch Function (32 + 1 outputs, 2-dp rounding) ===============
// Output map:
//  1  LU (1A02) CO2 avg        2  LU (1A02) CO2 raw         3  LU (1A02) Temp (°C)      4  LU (1A02) RH (%)        5  LU(1A02) Pressure (hPa)
//  6  RU (2A12) CO2 avg        7  RU (2A12) CO2 raw         8  RU (2A12) Temp (°C)      9  RU (2A12) RH (%)       10  RU (2A12) Pressure (hPa)
// 11  LD (1A01) CO2 avg       12  LD (1A01) CO2 raw        13  LD (1A01) Temp (°C)     14  LD (1A01) RH (%)       15  LD (1A01) Pressure (hPa)
// 16  RD (2A11) CO2 avg       17  RD (2A11) CO2 raw        18  RD (2A11) Temp (°C)     19  RD (2A11) RH (%)       20  RD (2A11) Pressure (hPa)
// 21  LEE600 (1A05)          22  REE600 (2A15)          23  LEE650T (1A08)(°C)     24  REE650T (2A18) (°C)    25  LEE650AV (1A08) (m/s)
// 26  REE650AV (2A18)(m/s)   27  LEM watts         28  REM watts        29  LPID block(1..15)  30  RPID block(1..15)
// 31  LVFD (array→splitter)                 32  2FN11 (array→splitter)
// 33  Mirrored scalar metrics for Grafana (/metrics flow)

// -------- helpers --------

// E+E float32 values are word-swapped (hi,lo -> lo,hi for bytes)
function readFloat32Swap(r0, r1) {
  const b = Buffer.alloc(4);
  b.writeUInt16BE(r1, 0); // low word
  b.writeUInt16BE(r0, 2); // high word
  return b.readFloatBE(0);
}
function f32at(regs, i) { return readFloat32Swap(regs[i], regs[i + 1]); }

// Generic Int32 (big-endian) from two holding registers
function readInt32BE(r0, r1) {
  const b = Buffer.alloc(4);
  b.writeUInt16BE(r0, 0);
  b.writeUInt16BE(r1, 2);
  return b.readInt32BE(0); // signed
}

// SDM230 (energy meter): float32, MS-register first (r0 = high reg, r1 = low reg)
function f32be(r0, r1) {
  const b = Buffer.alloc(4);
  b.writeUInt16BE(r0, 0); // high reg
  b.writeUInt16BE(r1, 2); // low  reg
  return b.readFloatBE(0);
}

// round to 2 decimals
function round2(v) { return Math.round(v * 100) / 100; }

const regs = msg.payload;
const tag = String(msg.topic || "");

if (!Array.isArray(regs)) {
  //node.error("Payload is not an array of registers", msg);
  return null;
}

// ===== MINIMAL CHANGE: make 33 outputs & add a mirror helper =====
const out = Array.from({ length: 33 }, () => null);
const out33 = [];
function mirror(topic, value, labels) {
  if (value == null || isNaN(value)) return;
  out33.push({ topic, payload: Number(value), labels: labels || {} });
}

// side helpers
function isLeftUp(t) { return /^LU/i.test(t); }
function isRightUp(t) { return /^RU/i.test(t); }
function isLeftDown(t) { return /^LD/i.test(t); }
function isRightDown(t) { return /^RD/i.test(t); }

// ---------- EE872: CO2 block (avg+raw at 0x424..0x427) (LU is 1A08; RU is 2A18; LD is 1A01; RD is 2A11) ----------
if (/EE872CO2$/i.test(tag)) {
  if (regs.length < 4) { node.error(`${tag} requires 4 registers`, msg); return null; }
  const co2_avg = round2(f32at(regs, 0)); // 0x424..0x425
  const co2_raw = round2(f32at(regs, 2)); // 0x426..0x427

  if (isLeftUp(tag)) {
    out[0] = { topic: "LU.CO2.avg", payload: co2_avg };
    out[1] = { topic: "LU.CO2.raw", payload: co2_raw };
    mirror("LU.CO2.avg", co2_avg, { zone: "lu" });
    mirror("LU.CO2.raw", co2_raw, { zone: "lu" });
  }
  else if (isRightUp(tag)) {
    out[5] = { topic: "RU.CO2.avg", payload: co2_avg };
    out[6] = { topic: "RU.CO2.raw", payload: co2_raw };
    mirror("RU.CO2.avg", co2_avg, { zone: "ru" });
    mirror("RU.CO2.raw", co2_raw, { zone: "ru" });
  }
  else if (isLeftDown(tag)) {
    out[10] = { topic: "LD.CO2.avg", payload: co2_avg };
    out[11] = { topic: "LD.CO2.raw", payload: co2_raw };
    mirror("LD.CO2.avg", co2_avg, { zone: "ld" });
    mirror("LD.CO2.raw", co2_raw, { zone: "ld" });
  }
  else if (isRightDown(tag)) {
    out[15] = { topic: "RD.CO2.avg", payload: co2_avg };
    out[16] = { topic: "RD.CO2.raw", payload: co2_raw };
    mirror("RD.CO2.avg", co2_avg, { zone: "rd" });
    mirror("RD.CO2.raw", co2_raw, { zone: "rd" });
  }
  else node.warn(`Unhandled CO2 tag: ${tag}`);

  out[32] = out33; // <- emit mirrored metrics
  return out;
}

// ---------- EE872: TRH wide block (Temp at 0x3EA..0x3EB, RH at 0x3FC..0x3FD) (LU is 1A08; RU is 2A18; LD is 1A01; RD is 2A11) ----------
if (/EE872TRH$/i.test(tag)) {
  if (regs.length < 20) { node.error(`${tag} requires 20 registers`, msg); return null; }
  const temperature_c = round2(f32at(regs, 0));   // 0x3EA..0x3EB
  const rh_percent = round2(f32at(regs, 18));     // 0x3FC..0x3FD

  if (isLeftUp(tag)) {
    out[2] = { topic: "LU.Temp.C", payload: temperature_c };
    out[3] = { topic: "LU.RH.%", payload: rh_percent };
    mirror("LU.Temp.C", temperature_c, { zone: "lu" });
    mirror("LU.RH.%", rh_percent, { zone: "lu" });
  }
  else if (isRightUp(tag)) {
    out[7] = { topic: "RU.Temp.C", payload: temperature_c };
    out[8] = { topic: "RU.RH.%", payload: rh_percent };
    mirror("RU.Temp.C", temperature_c, { zone: "ru" });
    mirror("RU.RH.%", rh_percent, { zone: "ru" });
  }
  else if (isLeftDown(tag)) {
    out[12] = { topic: "LD.Temp.C", payload: temperature_c };
    out[13] = { topic: "LD.RH.%", payload: rh_percent };
    mirror("LD.Temp.C", temperature_c, { zone: "ld" });
    mirror("LD.RH.%", rh_percent, { zone: "ld" });
  }
  else if (isRightDown(tag)) {
    out[17] = { topic: "RD.Temp.C", payload: temperature_c };
    out[18] = { topic: "RD.RH.%", payload: rh_percent };
    mirror("RD.Temp.C", temperature_c, { zone: "rd" });
    mirror("RD.RH.%", rh_percent, { zone: "rd" });
  }
  else node.warn(`Unhandled TRH tag: ${tag}`);

  out[32] = out33;
  return out;
}

// ---------- EE872: Pressure (0x4B0..0x4B1)(LU is 1A08; RU is 2A18; LD is 1A01; RD is 2A11) ----------
if (/EE872P$/i.test(tag)) {
  if (regs.length < 2) { node.error(`${tag} requires 2 registers`, msg); return null; }
  const pressure_hpa = round2(f32at(regs, 0));

  if (isLeftUp(tag)) {
    out[4] = { topic: "LU.P.hPa", payload: pressure_hpa };
    mirror("LU.P.hPa", pressure_hpa, { zone: "lu" });
  }
  else if (isRightUp(tag)) {
    out[9] = { topic: "RU.P.hPa", payload: pressure_hpa };
    mirror("RU.P.hPa", pressure_hpa, { zone: "ru" });
  }
  else if (isLeftDown(tag)) {
    out[14] = { topic: "LD.P.hPa", payload: pressure_hpa };
    mirror("LD.P.hPa", pressure_hpa, { zone: "ld" });
  }
  else if (isRightDown(tag)) {
    out[19] = { topic: "RD.P.hPa", payload: pressure_hpa };
    mirror("RD.P.hPa", pressure_hpa, { zone: "rd" });
  }
  else node.warn(`Unhandled Pressure tag: ${tag}`);

  out[32] = out33;
  return out;
}

// ---------- EE600 / EE650 (single float32 swap) (LEE650 is 1A08; REE650 is 2A18; LEE600 is 1A05; REE600 is 2A15) ----------
if (tag === "1A05" || tag === "2A15" || tag === "LEE650T" || tag === "REE650T" || tag === "LEE650AV" || tag === "REE650AV") {
  if (regs.length < 2) { node.error(`${tag} requires 2 registers`, msg); return null; }
  const val = round2(f32at(regs, 0));

  switch (tag) {
    case "1A05": out[20] = { topic: "1A05", payload: val }; mirror("1A05", val, { sensor: "1A05" }); break;
    case "2A15": out[21] = { topic: "2A15", payload: val }; mirror("2A15", val, { sensor: "2A15" }); break;
    case "LEE650T": out[22] = { topic: "LEE650T.C", payload: val }; mirror("LEE650T.C", val, { sensor: "LEE650T" }); break;
    case "REE650T": out[23] = { topic: "REE650T.C", payload: val }; mirror("REE650T.C", val, { sensor: "REE650T" }); break;
    case "LEE650AV": out[24] = { topic: "LEE650AV.mps", payload: val }; mirror("LEE650AV.mps",  val, { sensor: "LEE650AV"}); break;
    case "REE650AV": out[25] = { topic: "REE650AV.mps", payload: val }; mirror("REE650AV.mps",  val, { sensor: "REE650AV"}); break;

  }
  out[32] = out33;
  return out;
}

// ---------- Energy meters ----------
// SDM230 (LEM/REM): float32 in Input Registers (FC=4), MS-register first
if (tag === "LEM" || tag === "REM") {
  if (regs.length < 2) { node.error(`${tag} requires 2 registers`, msg); return null; }
  const watts = round2(f32be(regs[0], regs[1]));   // Active Power (W)
  if (tag === "LEM") { out[26] = { topic: "LEM.W", payload: watts }; mirror("LEM.W", watts, { sensor: "LEM" }); }
  else { out[27] = { topic: "REM.W", payload: watts }; mirror("REM.W", watts, { sensor: "REM" }); }
  out[32] = out33;
  return out;
}

// ---------- PIDs: pass-through full 15-register variables block (addr 1..15) ----------
if (tag === "LPID" || tag === "RPID") {
  if (regs.length < 15) { node.error(`${tag} requires 15 registers (addr 1..15)`, msg); return null; }
  const block = regs.slice(0, 15); // keep raw words; decode downstream

  if (tag === "LPID") out[28] = { topic: "LPID.block", payload: block };
  else out[29] = { topic: "RPID.block", payload: block };

  // Not mirrored to metrics (block/array). Add if you want.
  out[32] = out33;
  return out;
}

// ---------- VFDs (6 regs pass-through to your splitter nodes) ----------
if (tag === "LVFD" || tag === "2FN11") {
  if (regs.length < 6) { node.error(`${tag} requires 6 registers`, msg); return null; }
  const arr6 = regs.slice(0, 6);
  if (tag === "LVFD") out[30] = { topic: "LVFD", payload: arr6 };
  else out[31] = { topic: "2FN11", payload: arr6 };

  // Not mirrored to metrics (array). Add if you want.
  out[32] = out33;
  return out;
}

node.warn(`Decoder: unhandled tag "${tag}"`);
out[32] = out33;
return out;

'use strict';
// Save latest sensor metrics for Prometheus exposition
const store = flow.get('metrics') || {};

function classify(topic) {
  const N = String(topic || '').trim().toUpperCase();
  let metric = 'generic_gauge', help = 'Generic gauge';
  if (N.startsWith('LU.CO2.') || N.startsWith('RU.CO2.') || N.startsWith('LD.CO2.') || N.startsWith('RD.CO2.')) { metric = 'co2_ppm'; help = 'CO2 concentration'; }
  else if (/(^LU\.|^RU\.|^LD\.|^RD\.).*RH\.%$/.test(N)) { metric = 'humidity_percent'; help = 'Relative humidity'; }
  else if (/(^LU\.|^RU\.|^LD\.|^RD\.).*TEMP\.C$/.test(N)) { metric = 'temperature_celsius'; help = 'Air temperature'; }
  else if (/(^LU\.|^RU\.|^LD\.|^RD\.).*\.P\.HPA$/.test(N)) { metric = 'pressure_hpa'; help = 'Atmospheric pressure'; }
  else if (N === 'LEM.W' || N === 'REM.W') { metric = 'power_watts'; help = 'Active power'; }
  else if (/LEE650AV|REE650AV/.test(N)) { metric = 'air_velocity_mps'; help = 'Air velocity'; }
  else if (/LEE600|2A15/.test(N)) { metric = 'sensor_value'; help = 'Generic sensor value'; }
  return { metric, help };
}

const topic = String(msg.topic || '');
const val = Number(msg.payload);
if (!topic || !Number.isFinite(val)) return null;

const z = topic.match(/^(LU|RU|LD|RD)\b/i)?.[1]?.toLowerCase();
let sensor = '';
if (/LEE650AV|REE650AV/i.test(topic)) sensor = 'EE650AV';
else if (/LEE650T|REE650T/i.test(topic)) sensor = 'EE650T';
else if (/\bLEM\.W\b/i.test(topic)) sensor = 'LEM';
else if (/\bREM\.W\b/i.test(topic)) sensor = 'REM';

const { metric, help } = classify(topic);
const labels = {};
if (z) labels.zone = z;
if (sensor) labels.sensor = sensor;
const key = `${metric}|zone=${labels.zone || ''}|sensor=${labels.sensor || ''}`;
store[key] = { metric, value: val, labels, help };
flow.set('metrics', store);
return null;

'use strict';
const m = flow.get('metrics') || {};
const out = [];
const seen = new Set();
for (const k of Object.keys(m)) {
  const { metric, help } = m[k];
  if (!seen.has(metric)) {
    out.push(`# HELP ${metric} ${help}`);
    out.push(`# TYPE ${metric} gauge`);
    seen.add(metric);
  }
}
for (const k of Object.keys(m)) {
  const { metric, value, labels } = m[k];
  const labelStr = Object.entries(labels || {})
    .map(([kk, vv]) => `${kk}="${String(vv).replace(/"/g, '\\"')}"`)
    .join(',');
  out.push(labelStr ? `${metric}{${labelStr}} ${value}` : `${metric} ${value}`);
}
msg.headers = { 'Content-Type': 'text/plain; version=0.0.4; charset=utf-8' };
msg.payload = out.join('\n') + '\n';
return msg;

// Store the current slider value in flow.leftSliderValue
flow.set("leftSliderValue", msg.payload);

// Optional warning for debugging
//node.warn("Slider left value stored in flow context: " + msg.payload);

