flow.set("rightSliderValue", msg.payload);

// Optional warning for debugging
//node.warn("Slider Right value stored in flow context: " + msg.payload);

// Do not send anything downstream
return null;

if (msg.payload && msg.payload.address !== undefined && msg.payload.unitid !== undefined) {

    let val;

    // If value is provided in the payload, use it
    if (msg.payload.value !== undefined) {
        val = Number(msg.payload.value);
    }
    // Otherwise, get it from the stored slider value
    else {
        val = flow.get("leftSliderValue");
        if (val === undefined || isNaN(val)) {
            node.warn("Slider value not set or invalid, defaulting to 0");
            val = 0;
        }
        val = Number(val) * 100;
    }

    // Build Modbus packet (same as before)
    msg.payload = {
        fc: 6,
        unitid: Number(msg.payload.unitid),
        address: Number(msg.payload.address),
        quantity: 1,
        value: [val]
    };

    return msg;
}
// Ignore other messages
return null;

if (msg.payload && msg.payload.address !== undefined && msg.payload.unitid !== undefined) {

    let val;

    // If value is provided in the payload, use it
    if (msg.payload.value !== undefined) {
        val = Number(msg.payload.value);
    }
    // Otherwise, get it from the stored slider value
    else {
        val = flow.get("rightSliderValue");
        if (val === undefined || isNaN(val)) {
            node.warn("Slider value not set or invalid, defaulting to 0");
            val = 0;
        }
        val = Number(val) * 100;
    }

    // Build Modbus Flex Write payload
    msg.payload = {
        fc: 6,                               // Write Single Register
        unitid: Number(msg.payload.unitid),  // From button
        address: Number(msg.payload.address),// From button
        quantity: 1,
        value: [val]                         // From slider or fixed button value
    };

    return msg;
}

// Ignore other messages
return null;

// Expects: msg.payload = { address:<number>, unitid:<number> }
// Optional: msg.payload.value -> write this (integer)
// If address = SP (6..9 or 16): falls back to leftSliderPIDValue if no value
// If address = 15: Control Status (0=Auto,1=Manual,2=Standby)

if (msg && msg.payload && msg.payload.address !== undefined && msg.payload.unitid !== undefined) {
    if (typeof msg.payload === "string") {
        try { msg.payload = JSON.parse(msg.payload); }
        catch(e) { node.error("Button payload is not valid JSON", msg); return null; }
    }

    const unitid  = Number(msg.payload.unitid);
    const address = Number(msg.payload.address);

    if (!Number.isInteger(unitid) || unitid <= 0) { node.error("Invalid unitid", msg); return null; }
    if (!Number.isInteger(address) || address < 0) { node.error("Invalid address", msg); return null; }

    let val;

    if (msg.payload.value !== undefined) {
        val = Number(msg.payload.value);
    } else {
        // For SP registers only → fallback to slider
        if ([6,7,8,9,16].includes(address)) {
            val = flow.get("leftSliderPIDValue");
            if (val === undefined || isNaN(val)) {
                node.warn("leftSliderPIDValue not set, defaulting to 0");
                val = 0;
            }
            val = Math.round(val);
            if (val < 0) val = 0;
            if (val > 50) val = 50;
        } else {
            node.error("No value provided for non-SP address", msg);
            return null;
        }
    }

    // Build Flex Write
    msg.payload = {
        fc: 6,
        unitid,
        address,     // zero-based offset
        quantity: 1,
        value: [ val ]
    };

    node.status({ fill: "green", shape: "dot", text: `Write ${val} @U${unitid}:${address}` });
    return msg;
}

return null;

// Expects: msg.payload = { address:<number>, unitid:<number> }
// Optional: msg.payload.value -> write this (integer)
// If address = SP (6..9 or 16): falls back to rihghtSliderPIDValue if no value
