    } else {
        // For SP registers only → fallback to slider
        if ([6,7,8,9,16].includes(address)) {
            val = flow.get("rightSliderPIDValue");
            if (val === undefined || isNaN(val)) {
                node.warn("rightSliderPIDValue not set, defaulting to 0");
                val = 0;
            }
            val = Math.round(val);
            if (val < 0) val = 0;
            if (val > 60) val = 60;
        } else {
