        quantity: 1,
        value: [ val ]
    };

    node.status({ fill: "green", shape: "dot", text: `Write ${val} @U${unitid}:${address}` });
    return msg;
}

return null;

// Inputs:
// msg.payload = { relay: N, state: "ON" or "OFF", bankByte: <number> }

const relayNum = msg.payload.relay;
const state = msg.payload.state.toUpperCase();
const bankByte = msg.payload.bankByte;

if (relayNum < 1 || relayNum > 8) {
    node.error("Relay number must be 1 to 8", msg);
    return null;
}

if (typeof bankByte !== 'number') {
    node.error("bankByte must be a number", msg);
    return null;
}

const header = 0xAA;
const cmd1 = 0x03;
const cmd2 = 0xFE;

